global.script:
template = "migrate";
filial = (contextRequest.getRequest().getParam("filial")).toInteger();

if ( filial = 1001 || filial = 1002 || filial = 1007 ) 
{
	wasMigrated = "true";
	template = "response";
} 

else if ( filial = 1003 ) 
{
	wasMigrated = "false";
	template = "response";
} 

if (template == "response") 
{
	contextResponse.getTemplate(template).setParam("filial", filial);
	contextResponse.getTemplate(template).setParam("wasMigrated", wasMigrated);
}

return contextResponse.getTemplate(template);
